import React, { useState } from "react";
import { useMoralis, useWeb3ExecuteFunction } from "react-moralis";
import { Card, Image, Tooltip, Modal, Input } from "antd";
import { useNFTBalance } from "hooks/useNFTBalance";
import { FileSearchOutlined, ShoppingCartOutlined } from "@ant-design/icons";
import { useMoralisDapp } from "providers/MoralisDappProvider/MoralisDappProvider";
import { getExplorer } from "helpers/networks";
import AddressInput from "./AddressInput";
import { List } from "rc-field-form";
const { Meta } = Card;

const styles = {
  NFTs: {
    display: "flex",
    flexWrap: "wrap",
    WebkitBoxPack: "start",
    justifyContent: "flex-start",
    margin: "0 auto",
    maxWidth: "1000px",
    gap: "10px",
  },
};

function NFTBalance() {
  const { NFTBalance } = useNFTBalance();
  const { chainId, marketAddress, contractABI } = useMoralisDapp();
  const { Moralis } = useMoralis();
  const [visible, setVisibility] = useState(false);
  const [price, setPrice] = useState();
  const [nftToSell, setNftToSell] = useState(null);
  const [isPending, setIsPending] = useState(false);
  const contractProcessor = useWeb3ExecuteFunction();
  const contractABIJson = JSON.parse(contractABI);
  const listItemFunction = "createMarketItem";

  async function list(nft, currentPrice) {
    const p = currentPrice * ("1e" + 18);
    const ops = {
      contractAddress: marketAddress,
      functionName: listItemFunction,
      abi: contractABIJson,
      params: {
        nftContract: nft.token_address,
        tokenId: nft.token_id,
        price: String(p),
      },
    };

    await contractProcessor.fetch({
      params: ops,
      onSuccess: () => {
        alert("item bought");
        addItemImage();
      },
      onError: (error) => {
        alert("Something went wrong");
      },
    });
  }

  async function transfer(nft, amount, receiver) {
    const options = {
      type: nft.contract_type,
      tokenId: nft.token_id,
      receiver: receiver,
      contractAddress: nft.token_address,
    };

    if (options.type === "erc1155") {
      options.amount = amount;
    }

    setIsPending(true);
    await Moralis.transfer(options)
      .then((tx) => {
        console.log(tx);
        setIsPending(false);
      })
      .catch((e) => {
        alert(e.message);
        setIsPending(false);
      });
  }

  const handleSellClick = (nft) => {
    setNftToSell(nft);
    setVisibility(true);
  };

  function addItemImage() {
    const ItemImage = Moralis.Object.extend("ItemImages");
    const itemImage = new ItemImage();

    itemImage.set("image", nftToSell.image);
    itemImage.set("nftContract", nftToSell.token_address);
    itemImage.set("tokenId", nftToSell.token_id);
    itemImage.set("name", nftToSell.name);

    itemImage.save();
  }

  return (
    <>
      <div style={styles.NFTs}>
        {NFTBalance &&
          NFTBalance.map((nft, index) => (
            <Card
              hoverable
              actions={[
                <Tooltip title="View On Blockexplorer">
                  <FileSearchOutlined
                    onClick={() =>
                      window.open(
                        `${getExplorer(chainId)}address/${nft.token_address}`,
                        "_blank"
                      )
                    }
                  />
                </Tooltip>,
                // <Tooltip title="Transfer NFT">
                //   <SendOutlined onClick={() => handleTransferClick(nft)} />
                // </Tooltip>,
                <Tooltip title="Sell NFT">
                  <ShoppingCartOutlined onClick={() => handleSellClick(nft)} />
                </Tooltip>,
              ]}
              style={{ width: 240, border: "2px solid #e7eaf3" }}
              cover={
                <Image
                  preview={false}
                  src={nft?.image || "error"}
                  fallback="data:image/png;base64,"
                  alt=""
                  style={{ height: "300px" }}
                />
              }
              key={index}
            >
              <Meta title={nft.name} description={nft.token_id} />
            </Card>
          ))}
      </div>
      <Modal
        title={`Transfer ${nftToSell?.name || "NFT"}`}
        visible={visible}
        onCancel={() => setVisibility(false)}
        onOk={() => list(nftToSell, price)}
        confirmLoading={isPending}
        okText="Sell"
      >
        <img
          src={nftToSell?.image}
          alt=""
          style={{
            width: "250px",
            margin: "auto",
            borderRadius: "10px",
            marginBottom: "15px",
          }}
        />
        <Input
          autoFocus
          placeholder="Set Price in MATIC"
          onChange={(e) => setPrice(e.target.value)}
        />
      </Modal>
    </>
  );
}

export default NFTBalance;
