import React, { useState } from "react";
import { useMoralis, useMoralisQuery } from "react-moralis";
import { getNativeByChain } from "helpers/networks";
import { Card, Image, Tooltip, Modal, Badge, Alert } from "antd";
import { useNFTTokenIds } from "hooks/useNFTTokenIds";
import {
  FileSearchOutlined,
  ShoppingCartOutlined,
  RightCircleOutlined,
} from "@ant-design/icons";
import { useMoralisDapp } from "providers/MoralisDappProvider/MoralisDappProvider";
import { getExplorer } from "helpers/networks";
import { getCollectionsByChain } from "helpers/collections";
import { ethers } from 'ethers';
const { Meta } = Card;

const styles = {
  NFTs: {
    display: "flex",
    flexWrap: "wrap",
    WebkitBoxPack: "start",
    justifyContent: "flex-start",
    margin: "0 auto",
    maxWidth: "1000px",
    gap: "10px",
  },
};

/**
 * Displays all token ID's of an nft from helpers > collection.js
 * @param NA
 * @returns {*} JSX Elemenet
 */

function NFTTokenIds({ inputValue, setInputValue }) {
  const { NFTTokenIds } = useNFTTokenIds(inputValue);
  const { chainId, contractABI, marketAddress } =
    useMoralisDapp();
  const { Moralis } = useMoralis();
  const [visible, setVisibility] = useState(false);
  const nativeName = getNativeByChain(chainId);
  const [isPending, setIsPending] = useState(false);
  const NFTCollections = getCollectionsByChain(chainId);
  const [nftToBuy, setNftToBuy] = useState();
  const [price, setPrice] = useState();
  const queryMarketItems = useMoralisQuery("CreatedMarketItems");
  const fetchMarketItems = JSON.parse(
    JSON.stringify(queryMarketItems.data, [
      "objectId",
      "createdAt",
      "price",
      "nftContract",
      "itemId",
      "sold",
      "tokenId",
      "seller",
      "owner",
      "confirmed",
    ])
  );

  /**
   * Submits transaction to purchase an NFT from our marketplace
   * @param NA
   * @returns NA
   */
  async function purchase() {
    const tokenDetails = await getBuyData(nftToBuy);
    const itemID = tokenDetails.itemId;
    const tokenPrice = tokenDetails.price;
    console.log("ID:", itemID)
    console.log("price", tokenPrice)

    await window.ethereum.enable()
    const provider = new ethers.providers.Web3Provider(window.ethereum);
    const signer = provider.getSigner();

    const marketContract = new ethers.Contract(
      marketAddress,
      contractABI,
      signer
    );

    const options = {value: tokenPrice}
    const tx = await marketContract.createMarketSale(nftToBuy.token_address, itemID, options);
    console.log(tx);
  }

  /**
   * Submits transaction to transfer an NFT
   * @param {*} nft item
   * @param {string} address to send nft to
   * @returns {*} JSX Elemenet
   */
  async function transfer(nft, receiver) {
    const options = {
      type: nft.contract_type,
      tokenId: nft.token_id,
      receiver: receiver,
      contractAddress: nft.token_address,
    };

    setIsPending(true);
    await Moralis.transfer(options)
      .then((tx) => {
        console.log(tx);
        setIsPending(false);
      })
      .catch((e) => {
        alert(e.message);
        setIsPending(false);
      });
  }

  const handleBuyClick = (nft) => {
    setNftToBuy(nft);
    setVisibility(true);
  };

    /**
   * Get's data from our marketplpace to display Buy button if an NFT is on our matkeplace
   * @param {*} nft item
   * @returns {boolean} indicates if item is available for sale or not
   */

  async function getData(nft) {
    const provider = new ethers.providers.JsonRpcProvider(
      'https://speedy-nodes-nyc.moralis.io/74bc5da2f2b08db9a7b1b167/eth/rinkeby'
    )

    const marketContract = new ethers.Contract(
      marketAddress,
      contractABI,
      provider
    );

    const marketItems = await marketContract.getMarketItems();

    for(let item of marketItems) {
      console.log(nft?.token_id)
      if(item.nftContract === nft?.token_address ||
        ethers.utils.formatUnits(item.tokenId,0) === nft?.token_id) {
           setPrice(ethers.utils.formatEther(item.price));
           return true;
         }
    }
    setPrice("N/A")
    return false;
  }

    /**
   * Get's information on an item listed for sale in our marketplace contract
   * @param {*} nft item
   * @returns {<marketItem>[]} array of MarketItem struct from contract
   */
  async function getBuyData(nft) {
    const provider = new ethers.providers.JsonRpcProvider(
      'https://speedy-nodes-nyc.moralis.io/74bc5da2f2b08db9a7b1b167/eth/rinkeby'
    )

    const marketContract = new ethers.Contract(
      marketAddress,
      contractABI,
      provider
    );

    const marketItems = await marketContract.getMarketItems();
    for(let item of marketItems) {
      if(ethers.utils.formatUnits(item.tokenId,0) === nft?.token_id) {
           return item;
         }
    }
  }

  const getMarketItem = (nft) => {
    const result = fetchMarketItems?.find(
      (e) =>
        e.nftContract === nft?.token_address &&
        e.tokenId === nft?.token_id &&
        e.sold === false &&
        e.confirmed === true
    );
    return result;
  };

  return (
    <>
      <div style={styles.NFTs}>
        {NFTTokenIds &&
          inputValue !== "explore" &&
          NFTTokenIds.map((nft, index) => (
            <Card
              hoverable
              actions={[
                <Tooltip title="View On Blockexplorer">
                  <FileSearchOutlined
                    onClick={() =>
                      window.open(
                        `${getExplorer(chainId)}address/${nft.token_address}`,
                        "_blank"
                      )
                    }
                  />
                </Tooltip>,
                // <Tooltip title="Transfer NFT">
                //   <SendOutlined onClick={() => handleTransferClick(nft)} />
                // </Tooltip>,
                <Tooltip title="Buy NFT">
                  <ShoppingCartOutlined onClick={() => handleBuyClick(nft)} />
                </Tooltip>,
              ]}
              style={{ width: 240, border: "2px solid #e7eaf3" }}
              cover={
                <Image
                  preview={false}
                  src={nft?.image || "error"}
                  fallback="data:image/png;base64,"
                  alt=""
                  style={{ height: "300px" }}
                />
              }
              key={index}
            >
              {getMarketItem(nft) && (
                <Badge.Ribbon text="Buy Now" color="green" />
              )}
              <Meta title={nft.name} description={`#${nft.token_id}`} />
            </Card>
          ))}

        {NFTCollections &&
          inputValue === "explore" &&
          NFTCollections.map((nft, index) => (
            <Card
              hoverable
              actions={[
                <Tooltip title="View This Collection">
                  <RightCircleOutlined
                    onClick={() => {setInputValue(nft.addrs);}}
                  />
                </Tooltip>,
              ]}
              style={{ width: 240, border: "2px solid #e7eaf3" }}
              cover={
                <Image
                  preview={false}
                  src={nft?.image || "error"}
                  fallback="data:image/png;base64,"
                  alt=""
                  style={{ height: "300px" }}
                />
              }
              key={index}
            >
              <Meta title={nft.name} />
            </Card>
          ))}
      </div>
      {getData(nftToBuy) ? (
        <Modal
          title={`Transfer ${nftToBuy?.name || "NFT"}`}
          visible={visible}
          onCancel={() => setVisibility(false)}
          onOk={() => purchase()}
          confirmLoading={isPending}
          okText="Buy"
        >
          <div
            style={{
              width: "250px",
              margin: "auto",
            }}
          >
            <Badge.Ribbon
              text={`${
                price// / ("1e" + 18)
              } ${nativeName}`}
              color="green"
            />
            <img
              src={nftToBuy?.image}
              style={{
                width: "250px",
                margin: "auto",
                borderRadius: "10px",
                marginBottom: "15px",
              }}
            />
          </div>
        </Modal>
      ) : (
        <Modal
          title={`Transfer ${nftToBuy?.name || "NFT"}`}
          visible={visible}
          onCancel={() => setVisibility(false)}
          onOk={() => setVisibility(false)}
          confirmLoading={isPending}
          okText="Ok"
        >
          <img
            src={nftToBuy?.image}
            style={{
              width: "250px",
              margin: "auto",
              borderRadius: "10px",
              marginBottom: "15px",
            }}
          />
          <Alert message="This NFT is not available for sale" type="warning" />
        </Modal>
      )}
    </>
  );
}

export default NFTTokenIds;
